import React from 'react'
import content from '../content'


const items = [
  {
    title: "What is react?",
    content: "Front end framework"
  },
  {
    title: "What is you name?",
    content: "my name is shubam"
  },
  {
    title: "What is very important in React?",
    content: "Components"
  }
];



const Accordion = () => {
  return (
    <>
       <h1>Simple React Hooks Accordion</h1>
      <p>With animations</p>

      <Accordion>
        {content.map(({ question, answer }, index) => (
          <Accordion.Item key={index}>
            <Accordion.Toggle eventKey={index}>{question}</Accordion.Toggle>
            <Accordion.Collapse eventKey={index}>{answer}</Accordion.Collapse>
          </Accordion.Item>
        ))}
      </Accordion>
      <Accordion items={items} />
    </>
  )
}

export default Accordion
